import React, { useState } from 'react';

const Flipbook = () => {
  const [currentPage, setCurrentPage] = useState(0);

  const pages = [
    'Page 1 content',
    'Page 2 content',
    'Page 3 content',
    'Page 4 content',
  ];

  const nextPage = () => {
    if (currentPage < pages.length - 1) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 0) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="flipbook">
      <div className="flipbook-page">
        {pages.map((page, index) => (
          <div
            key={index}
            className={`page ${index === currentPage ? 'current' : ''}`}
          >
            {page}
          </div>
        ))}
      </div>
      <div className="flipbook-controls">
        <button className='buttonFlip' onClick={prevPage} disabled={currentPage === 0}>
          Previous
        </button>
        <button className='buttonFlip' onClick={nextPage} disabled={currentPage === pages.length - 1}>
          Next
        </button>
      </div>
    </div>
  );
};

export default Flipbook;
