import React, { useEffect } from 'react';
import $ from 'jquery';
import 'jquery-ui-dist/jquery-ui';
import "C:/Users/Syahrul/PKM/test-react/portfolio-alpha/src/lib/turn.js";
import "C:/Users/Syahrul/PKM/test-react/portfolio-alpha/src/lib/zoom.js";
import Modernizr from 'modernizr'; // Assuming Modernizr is available via npm
import yepnope from 'yepnope'; // Assuming yepnope is available via npm

const Flipbook = () => {
  useEffect(() => {
    const loadApp = () => {
      $('#canvas').fadeIn(1000);

      const flipbook = $('.magazine');

      if (flipbook.width() === 0 || flipbook.height() === 0) {
        setTimeout(loadApp, 10);
        return;
      }

      flipbook.turn({
        width: 922,
        height: 600,
        duration: 1000,
        gradients: true,
        autoCenter: true,
        elevation: 50,
        pages: 12,
        when: {
          turning(event, page) {
            const book = $(this);
            const currentPage = book.turn('page');
            const pages = book.turn('pages');

            Hash.go('page/' + page).update();

            disableControls(page);
          },
          turned(event, page) {
            disableControls(page);
            $(this).turn('center');
            $('#slider').slider('value', getViewNumber($(this), page));
            if (page === 1) {
              $(this).turn('peel', 'br');
            }
          },
          missing(event, pages) {
            for (let i = 0; i < pages.length; i++) addPage(pages[i], $(this));
          },
        },
      });

      $('.magazine-viewport').zoom({
        flipbook: $('.magazine'),
        max() {
          return largeMagazineWidth() / $('.magazine').width();
        },
        when: {
          swipeLeft() {
            $(this).zoom('flipbook').turn('next');
          },
          swipeRight() {
            $(this).zoom('flipbook').turn('previous');
          },
          resize(event, scale, page, pageElement) {
            if (scale === 1) loadSmallPage(page, pageElement);
            else loadLargePage(page, pageElement);
          },
          zoomIn() {
            $('#slider-bar').hide();
            $('.made').hide();
            $('.magazine').removeClass('animated').addClass('zoom-in');
            $('.zoom-icon').removeClass('zoom-icon-in').addClass('zoom-icon-out');

            if (!window.escTip && !$.isTouch) {
              escTip = true;

              $('<div />', { class: 'exit-message' })
                .html('<div>Press ESC to exit</div>')
                .appendTo($('body'))
                .delay(2000)
                .animate({ opacity: 0 }, 500, function () {
                  $(this).remove();
                });
            }
          },
          zoomOut() {
            $('#slider-bar').fadeIn();
            $('.exit-message').hide();
            $('.made').fadeIn();
            $('.zoom-icon').removeClass('zoom-icon-out').addClass('zoom-icon-in');

            setTimeout(function () {
              $('.magazine').addClass('animated').removeClass('zoom-in');
              resizeViewport();
            }, 0);
          },
        },
      });

      if ($.isTouch) $('.magazine-viewport').bind('zoom.doubleTap', zoomTo);
      else $('.magazine-viewport').bind('zoom.tap', zoomTo);

      $(document).keydown(function (e) {
        const previous = 37,
          next = 39,
          esc = 27;

        switch (e.keyCode) {
          case previous:
            $('.magazine').turn('previous');
            e.preventDefault();
            break;
          case next:
            $('.magazine').turn('next');
            e.preventDefault();
            break;
          case esc:
            $('.magazine-viewport').zoom('zoomOut');
            e.preventDefault();
            break;
        }
      });

      Hash.on('^page\/([0-9]*)$', {
        yep: function (path, parts) {
          const page = parts[1];

          if (page !== undefined) {
            if ($('.magazine').turn('is')) $('.magazine').turn('page', page);
          }
        },
        nop: function (path) {
          if ($('.magazine').turn('is')) $('.magazine').turn('page', 1);
        },
      });

      $(window)
        .resize(function () {
          resizeViewport();
        })
        .bind('orientationchange', function () {
          resizeViewport();
        });

      if ($.isTouch) {
        $('.magazine').bind('touchstart', regionClick);
      } else {
        $('.magazine').click(regionClick);
      }

      $('.next-button')
        .bind($.mouseEvents.over, function () {
          $(this).addClass('next-button-hover');
        })
        .bind($.mouseEvents.out, function () {
          $(this).removeClass('next-button-hover');
        })
        .bind($.mouseEvents.down, function () {
          $(this).addClass('next-button-down');
        })
        .bind($.mouseEvents.up, function () {
          $(this).removeClass('next-button-down');
        })
        .click(function () {
          $('.magazine').turn('next');
        });

      $('.previous-button')
        .bind($.mouseEvents.over, function () {
          $(this).addClass('previous-button-hover');
        })
        .bind($.mouseEvents.out, function () {
          $(this).removeClass('previous-button-hover');
        })
        .bind($.mouseEvents.down, function () {
          $(this).addClass('previous-button-down');
        })
        .bind($.mouseEvents.up, function () {
          $(this).removeClass('previous-button-down');
        })
        .click(function () {
          $('.magazine').turn('previous');
        });

      $('#slider').slider({
        min: 1,
        max: numberOfViews(flipbook),
        start: function (event, ui) {
          if (!window._thumbPreview) {
            _thumbPreview = $('<div />', { class: 'thumbnail' }).html('<div></div>');
            setPreview(ui.value);
            _thumbPreview.appendTo($(ui.handle));
          } else setPreview(ui.value);

          moveBar(false);
        },
        slide: function (event, ui) {
          setPreview(ui.value);
        },
        stop: function () {
          if (window._thumbPreview) _thumbPreview.removeClass('show');
          $('.magazine').turn('page', Math.max(1, $(this).slider('value') * 2 - 2));
        },
      });

      resizeViewport();

      $('.magazine').addClass('animated');
    };

    const addPage = (page, book) => {
      // Implementation for adding a page
    };

    const disableControls = (page) => {
      // Implementation for disabling controls
    };

    const resizeViewport = () => {
      // Implementation for resizing viewport
    };

    const zoomTo = (event) => {
      // Implementation for zooming
    };

    const largeMagazineWidth = () => {
      return 1600;
    };

    const loadSmallPage = (page, pageElement) => {
      // Implementation for loading small page
    };

    const loadLargePage = (page, pageElement) => {
      // Implementation for loading large page
    };

    const setPreview = (page) => {
      // Implementation for setting preview
    };

    const moveBar = (yes) => {
      // Implementation for moving bar
    };

    const regionClick = (event) => {
      // Implementation for region click
    };

    const getViewNumber = (book, page) => {
      return Math.round(page / 2);
    };

    const escTip = false;

    // Zoom icon
    $('.zoom-icon')
      .bind('mouseover', function () {
        if ($(this).hasClass('zoom-icon-in')) $(this).addClass('zoom-icon-in-hover');
        if ($(this).hasClass('zoom-icon-out')) $(this).addClass('zoom-icon-out-hover');
      })
      .bind('mouseout', function () {
        if ($(this).hasClass('zoom-icon-in')) $(this).removeClass('zoom-icon-in-hover');
        if ($(this).hasClass('zoom-icon-out')) $(this).removeClass('zoom-icon-out-hover');
      })
      .bind('click', function () {
        if ($(this).hasClass('zoom-icon-in')) $('.magazine-viewport').zoom('zoomIn');
        else if ($(this).hasClass('zoom-icon-out')) $('.magazine-viewport').zoom('zoomOut');
      });

    $('#canvas').hide();

    yepnope({
      test: Modernizr.csstransforms,
      yep: ['../../lib/turn.min.js'],
      nope: ['../../lib/turn.html4.min.js', 'css/jquery.ui.html4.css'],
      both: ['../../lib/zoom.min.js', 'css/jquery.ui.css', 'js/magazine.js', 'css/magazine.css'],
      complete: loadApp,
    });
  }, []);

  return <div id="canvas"></div>;
};

export default Flipbook;
