// src/components/Flipbook.js
import React from 'react';
import { PageFlip } from 'page-flip';

const Flipbook = ({ images }) => {
    const flipbookRef = React.useRef();

    React.useEffect(() => {
        const flipbook = new PageFlip(flipbookRef.current, {
            width: 400,
            height: 300,
        });

        images.forEach((image) => {
            flipbook.addPage(image);
        });

        flipbook.load();
    }, [images]);

    return <div ref={flipbookRef} />;
};

export default Flipbook;
